import './App.css';
import React from 'react';

class App extends React.Component {
  constructor(props){
    super(props);
      this.state={
        todoList:[],
        activeItem:{
          id:null,
          title:'',
          compleated:false,
        },
        editing:false,
      }
      this.fetchTasks=this.fetchTasks.bind(this)
      this.handleChange=this.handleChange.bind(this)
      this.handleSubmit=this.handleSubmit.bind(this)
      this.getCookie=this.get.bind(this)

  };

  getCookie(name) {
    let cookieValue = null;
    if (document.cookie && document.cookie !== '') {
        const cookies = document.cookie.split(';');
        for (let i = 0; i < cookies.length; i++) {
            const cookie = cookies[i].trim();
            // Does this cookie string begin with the name we want?
            if (cookie.substring(0, name.length + 1) === (name + '=')) {
                cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                break;
            }
        }
    }
    return cookieValue;
  }

  componentWillMount() {
    this.fetchTasks()

  }
  fetchTasks(){
    console.log("fetching....");
    fetch("http://127.0.0.1:8000/api/tasks")
    .then(response=>response.json())
    .then(data=>
      this.setState({
        todoList:data
      })
      )
  }
  handleChange(e){
    var name=e.target.name;
    var value=e.target.value;
    console.log('name',name)
    console.log('val',value)

    this.setState({
      activeItem:{
        ...this.state.activeItem,
        title:value
      }
    })

  }

  handleSubmit(e){
    e.preventDefault();
    console.log('ite,',this.state.activeItem)
    var url="http://127.0.0.1:8000/api/create"
    fetch(url,{
      method:'POST',
      headers:{
        'Content-type':'application/json',

      },
      body:JSON.stringify(this.state.activeItem)
    }).then((response) =>{
      this.fetchTasks()
      this.setState({
        activeItem:{
          id:null,
          title:'',
          compleated:false,
        }
      })
      }).catch(function(error){
        console.log("error",error)
    })
  }

  render() {
    var tasks=this.state.todoList
    return(
      <div className="container">
        <div id="task-container">
          <div id="form-wrapper">
            <form onSubmit={this.handleSubmit} id="form">
              <div className="flex-wrapper">
                <div style={{ flex:6}}>
                  <input onChange={this.handleChange} className="form-control" id="title" type="text" name="title" placeholder="Add task..."/>
                </div>
                <div style={{ flex:1}}>
                  <input className="btn btn-warning" id="submit" type="submit" name="Add"/>
                </div>

              </div>
            </form>

          </div>
          <div id="list-wrapper">
            {tasks.map(function(task,index){
              return (
                <div key={index} className="task-wrapper flex-wrapper">
                  <div style={{ flex:7}}>
                    <span>{task.title}</span>
                  </div>
                  <div style={{ flex:1}}>
                    <button className="btn btn-sm btn-outline-info">Edit</button>
                  </div>
                  <div style={{ flex:1}}>
                    <button className="btn btn-sm btn-outline-dark delete">-</button>
                  </div>
                </div>
              )
            })}

          </div>
        </div>

      </div>
    )
  }
}

export default App;
